// Importing React since we are using React.
import React from 'react';
// Importing UI components from material-ui-next
import Typography from 'material-ui/Typography';
import { MenuItem } from 'material-ui/Menu';
import { FormControl } from 'material-ui/Form';
import Button from 'material-ui/Button';
import TextField from 'material-ui/TextField';
import Card, { CardContent } from 'material-ui/Card';
import { withStyles } from 'material-ui/styles';
import { InputLabel } from 'material-ui/Input';
import Tooltip from 'material-ui/Tooltip';
import IconButton from 'material-ui/IconButton';
import DateFnsUtils from "@date-io/date-fns"; // import
import { DatePicker, MuiPickersUtilsProvider } from "@material-ui/pickers";
// Style for add appointments form.
const styles = {
  textField: {
    marginTop: 60,
  },
  // Tell Material-UI what's the font-size on the html element is.
  typography: {
    htmlFontSize: 40,
  },
  root: {
    display: 'flex',
    flexWrap: 'wrap',
    borderStyle: 'solid',
    borderWidth: 4,
    borderColor: '#33658A',
    maxWidth: '85%',
  },
  formControl: {
    minWidth: 120,
    marginTop: 30,
  },
  button: {
    marginTop: 20,
    padding: 15,
    backgroundColor: '#33658A',
    color: 'white',
  },
  formError: {
    color: 'red',
  },
  formSuccess: {
    color: 'green',
    marginTop: 10,
  }
};

class AddCar extends React.Component {
  state = {
    model: '',
    year: '',
    make: ''
  };
  
  handleCarSubmit = event => {
    this.setState({ [event.target.name]: event.target.value });
    event.preventDefault();
    this.props.handleAppointmentDealerChange(event);
  }

  render() {
    const { classes, Dealers, clinics } = this.props;


    return (
      <div>
        <Card className={classes.root}>
          <CardContent>
            <Typography gutterBottom variant="headline" component="h2">
            Cars
            </Typography>
            <form noValidate autoComplete="off" id="addcar-form">
              <FormControl className={classes.formControl} fullWidth>
                <InputLabel htmlFor="appointment-name">
                  <span>
                    Car Make
                    <Tooltip  
                      title="Specify a name for the cars make"
                      placement="top">
                      <IconButton> <i className="material-icons">help</i></IconButton>
                    </Tooltip>
                  </span>
                </InputLabel>
                <TextField
                  id="appointment-name"
                  InputLabelProps={{
                    shrink: true,
                  }}
                  margin="normal"
                  className={classes.textField}
                  value={this.props.appointmentName}
                  onChange={this.props.handleAppointmentNameChange}
                />
                <Typography className={classes.formError} component="p">{this.props.appointmentNameError}</Typography>
              </FormControl>

              <FormControl className={classes.formControl} fullWidth>
                <InputLabel htmlFor="select-Dealer">
                  <span>
                    Car Model
                    <Tooltip  
                      title="Specify the cars model"
                      placement="top">
                      <IconButton> <i className="material-icons">help</i></IconButton>
                    </Tooltip>
                  </span>
                </InputLabel>
                <TextField 
                  id='Dealer'
                  select
                  InputLabelProps={{
                    shrink: true,
                  }}
                  className={classes.textField} 
                  value={this.state.value}
                  onChange={this.handleDealerMenuOption} 
                  SelectProps={{ name: 'value'}} 
                  margin="normal">
                    
                  </TextField>
                  <Typography className={classes.formError} component="p">{this.props.appointmentDealerError}</Typography>
              </FormControl>
                
              <FormControl className={classes.formControl} fullWidth>
                <InputLabel>
                  <span>
                    Car Year
                    <Tooltip  
                      title="Specify the cars year"
                      placement="top">
                      <IconButton> <i className="material-icons">help</i></IconButton>
                    </Tooltip>
                  </span>
                </InputLabel>
                
                <Typography className={classes.formError} component="p">{this.props.appointmentTimeError}</Typography>
              </FormControl>

              <Button 
                size="large" 
                className={classes.button} 
                onClick={this.props.handleFormSubmit}
                color="primary" 
                variant="raised"
              >
                Add Car
              </Button>
              <Typography className={classes.formSuccess} component="p">{this.props.formSuccessMessage}</Typography>
            </form>
          </CardContent>
        </Card>
      </div>
    );
  }
}

export default withStyles(styles)(AddCar);
